mapper.insert:
insert into role (id, role_name, role_desc
  )
values (#{id,jdbcType=BIGINT}, #{roleName,jdbcType=VARCHAR}, #{roleDesc,jdbcType=VARCHAR}
  )

mapper.update:
update role
set role_name = #{roleName,jdbcType=VARCHAR},
  role_desc = #{roleDesc,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, role_name, role_desc
from role
where id = #{id,jdbcType=BIGINT}

SELECT id,authority_name,authority_desc FROM authority WHERE id IN
  (SELECT authority_id FROM role_authority WHERE role_id IN
    (SELECT role_id FROM user_role WHERE user_id = 1));
